# function declarationdef median(visits):    # sorting the values    visits.sort()    # getting the mid values    mid = len(visits) // 2    # calculating the median    res = (visits[mid] + visits[~mid]) / 2    # returting the result    return str(res)# initializing the valuesvisits = [6.9,1.6,2,7.4,8.8,1.5,3.6,13.1,7.9,8.6,9.2,-5.8,-3.4,-4.5,-3,3.8,-2.5,-1.5,2.8,6.4,-16.1]# funtion callprint(median(visits))